import { Component } from 'react';
import Video from './components/Video/Video';
import UnderVideoSection from './components/UnderVideoSection/UnderVideoSection';

class App extends Component {

  newId = (id) => {
    this.setState({videoId: id})
  }

  state = {
    videoId: VideoDetails[0].id
  };

  render() {
    const activeVideo = VideoDetails.findIndex(element => element.id === this.state.videoId);

    return (
      <div>
      <Header />
      <Video activeVideo={VideoDetails[activeVideo]} />
      <UnderVideoSection activeVideo={props.activeVideo}/>
      </div>
    )
  }
}

export default App;
